 /* START: Maintain video aspect ratio 16:9 */
 $(document).ready(function() {
  var w = $("#articles-div").width();
  var f_h = (w/16)*9;
  
  $("#multimedia, #videoDiv-1, #videoDiv-2, #videoDiv-3, #videoDiv-4, #videoDiv-5").css({"width": w, "height": f_h});

  $(window).resize(function() {
   w = $("#articles-div").width();
   f_h = (w/16)*9;
  $("#multimedia, #videoDiv-1, #videoDiv-2, #videoDiv-3, #videoDiv-4, #videoDiv-5").css({"width": w, "height": f_h});
  if ($(window).width() < 850) {  
  $('#stickThis').removeClass('stick');
  $('#stick-here').height(0); 
  } 
});
});
/* END: Maintain video ratio 16:9 */

